css:
:host atom-text-editor .highlight.highlight-selected .region,
atom-text-editor atom-text-editor .highlight.highlight-selected .region,
atom-text-editor::shadow atom-text-editor .highlight.highlight-selected .region {
  position: absolute;
  pointer-events: none;
  z-index: -1;
}
:host .highlights .highlight-selected .region,
atom-text-editor .highlights .highlight-selected .region,
atom-text-editor::shadow .highlights .highlight-selected .region {
  border-radius: 3px;
  box-sizing: border-box;
  background-color: transparent;
  border-width: 1px;
  border-style: solid;
}
:host .highlights .highlight-selected .region,
atom-text-editor .highlights .highlight-selected .region,
atom-text-editor::shadow .highlights .highlight-selected .region {
  border-color: #ddd;
}
:host .highlights .highlight-selected.background .region,
atom-text-editor .highlights .highlight-selected.background .region,
atom-text-editor::shadow .highlights .highlight-selected.background .region {
  background-color: rgba(155, 149, 0, 0.6);
}
:host .highlights .highlight-selected.light-theme .region,
atom-text-editor .highlights .highlight-selected.light-theme .region,
atom-text-editor::shadow .highlights .highlight-selected.light-theme .region {
  border-color: rgba(255, 128, 64, 0.4);
}
:host .highlights .highlight-selected.light-theme.background .region,
atom-text-editor .highlights .highlight-selected.light-theme.background .region,
atom-text-editor::shadow .highlights .highlight-selected.light-theme.background .region {
  background-color: rgba(255, 128, 64, 0.2);
}
